import React, { Component } from 'react';
import Feed from '../Layout/Feed'
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import Sidebar from '../Layout/Sidebar'
import { Row, Col } from 'antd';
import '../../App.css'
import { redirect } from 'react-router-dom'

class Home extends Component {

    constructor(props){
        super(props)
        this.state = {
            query: 'Default'
        }
    }

    componentDidMount(){
        this.getQuery()
    }

    getQuery(){
        if(this.props.location.state){
            this.setState({
                query: this.props.location.state.query
            })
        }
    }

    render() {
        const { posts, auth } = this.props;
        //if (!auth.uid) return <redirect to= '/signin'/> //Use for actions that the user cant complete unless they are signed in
        return (
            <div id="home">
                <Row>
                    <Col xs={12} md={4}>
                        <Sidebar query={this.updateQuery}/>
                    </Col>
                    <Col xs={12} sm={18} offset={1}  id="feed-container">
                        <Feed posts = {posts}/>
                    </Col>
                </Row>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    console.log(state)
    return {
        posts: state.firestore.ordered.posts,
        auth: state.firebase.auth,
        query: state.query
    }
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([{ 
        collection: 'posts',
        where: [
            'genre', '==', window.location.pathname.split("/").pop()
        ]}
    ])
)(Home)
