import React, { Component } from 'react'
import { connect } from 'react-redux'
import { createPost } from '../../Store/Actions/postActions'
import { Form, Icon, Input, Button, Select, notification } from 'antd';
import { redirect } from 'react-router-dom'

class NewPostForm extends Component {

    constructor() {
        super();
        this.state = {
            title: '',
            content: '',
            genre: ''
        }
        this.closeModal = this.closeModal.bind(this)
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    handleSelectChange = (e) => {
        this.setState({
            genre: e
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state);
        this.props.createPost(this.state)
        notification.open({
            message: 'Prompt Posted!',
            description: 'I bet its really great man...',
            duration: 2
        })
        this.closeModal()
    }

    closeModal(){
        return this.props.close
    }

    render() {
        const { auth } = this.props;
        //if (!auth.uid) return <redirect to= '/signin'/> //Use for actions that the user cant complete unless they are signed in
        const { TextArea } = Input;
        const { Option } = Select;

        return (

            <Form onSubmit={this.handleSubmit} className="login-form">

                <Form.Item>
                    <Input
                        id="title"
                        onChange={this.handleChange} 
                        placeholder="Title"
                    />
                </Form.Item>

                <Form.Item>
                    <TextArea
                        id="content" 
                        onChange={this.handleChange}
                        rows={5}
                        placeholder="Content"
                    />
                </Form.Item>

                <Form.Item>
                    <Select
                        showSearch
                        style={{ width: 200 }}
                        placeholder="Category"
                        onChange={this.handleSelectChange}
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                            option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        <Option value="Comedy">Comedy</Option>
                        <Option value="Drama">Drama</Option>
                        <Option value="Romance">Romance</Option>
                        <Option value="Scifi">Sci-Fi</Option>

                    </Select>
                </Form.Item>

                <Button type="primary" htmlType="submit">    
                    <Icon type="plus"/>
                    Post Prompt
                </Button>

            </Form>
    )
  }
}

const mapStateToProps = (state) => {
    return {
        auth: state.firebase.auth
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createPost: (post) => dispatch(createPost(post))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NewPostForm)
