import React, { Component } from 'react';
import { Card, Icon, Avatar, Tag, Popover } from 'antd';
import '../../App.css'

class Prompt extends Component {

    getTime(){
        var time = this.props.time
        if(time){
            var today = new Date()
            var current = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate()+' - '+today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds()
            console.log(current - time)
            return <Tag color="blue">34h Left</Tag>
        }else{
            return <Tag>No Time</Tag>
        }
    }

    render() {
        const { Meta } = Card;
        return (
            <Card
                actions={[
                    <Icon type="heart" key="heart" />,
                    <Icon type="book" key="book" />,
                    <Icon type="user" key="user" />,
                ]}
            >
                <Meta
                    avatar={
                        <span>
                            <Popover content={'@' + this.props.author} title="">
                                <Avatar icon="user" />
                            </Popover>
                        </span>
                    }
                    title = {
                        <span id="title-container">
                            <span id="card-title">{this.props.title}</span>
                            {this.getTime()}
                        </span>
                    }
                    description =  { 
                        <div>
                            <small>{ this.props.genre }</small>
                            <br></br><br></br>
                            <span id="card-content">{ this.props.content }</span>
                            <br></br>
                        </div>
                    }
                />
            </Card>

        );
    }
}

export default Prompt;
